INSERT INTO tipo_amenaza (pk,cod,nombre,descripcion,fk_superior) VALUES (1,'N','Desastres naturales','Sucesos que pueden ocurrir sin intervención de los seres humanos como causa directa o indirecta.Origen: Natural (accidental)',NULL);
INSERT INTO tipo_amenaza (pk,cod,nombre,descripcion,fk_superior) VALUES (2,'N.1','Fuego','incendios: posibilidad de que el fuego acabe con recursos del sistema. Ver: EBIOS: 01- INCENDIO',1);
INSERT INTO tipo_amenaza (pk,cod,nombre,descripcion,fk_superior) VALUES (3,'N.2','Daños por agua','inundaciones: posibilidad de que el agua acabe con recursos del sistema. Ver: EBIOS: 02 - PERJUICIOS OCASIONADOS POR EL AGUA',1);
INSERT INTO tipo_amenaza (pk,cod,nombre,descripcion,fk_superior) VALUES (4,'N.*','Desastres naturales','otros incidentes que se producen sin intervención humana: rayo, tormenta eléctrica, terremoto, ciclones, avalancha, corrimiento de tierras, ... Se excluyen desastres específicos tales como incendios (ver [N.1]) e inundaciones (ver [N.2]). Se excluye al personal por cuanto se ha previsto una amenaza específica [E.31] para cubrir la indisponibilidad involuntaria del personal sin entrar en sus causas. Ver: EBIOS: 03 – CONTAMINACIÓN 04 - SINIESTRO MAYOR 06 - FENÓMENO CLIMÁTICO 07 - FENÓMENO SÍSMICO 08 - FENÓMENO DE ORIGEN VOLCÁNICO 09 - FENÓMENO METEOROLÓGICO 10 - INUNDACIÓN',1);
INSERT INTO tipo_amenaza (pk,cod,nombre,descripcion,fk_superior) VALUES (5,'I','De origen industrial','Sucesos que pueden ocurrir de forma accidental, derivados de la actividad humana de tipo industrial. Estas amenazas puede darse de forma accidental o deliberada.',null);
INSERT INTO tipo_amenaza (pk,cod,nombre,descripcion,fk_superior) VALUES (6,'I.1','Fuego','incendio: posibilidad de que el fuego acabe con los recursos del sistema. Origen: Entorno (accidental) Humano (accidental o deliberado) Ver: EBIOS: 01- INCENDIO',1);
INSERT INTO tipo_amenaza (pk,cod,nombre,descripcion,fk_superior) VALUES (7,'I.2','Daños por agua','escapes, fugas, inundaciones: posibilidad de que el agua acabe con los recursos del sistema. Origen: Entorno (accidental) Humano (accidental o deliberado) Ver: EBIOS: 02 - PERJUICIOS OCASIONADOS POR EL AGUA',1);
INSERT INTO tipo_amenaza (pk,cod,nombre,descripcion,fk_superior) VALUES (9,'I.*','Desastres industriales','otros desastres debidos a la actividad humana: explosiones, derrumbes, ... contaminación química, ... sobrecarga eléctrica, fluctuaciones eléctricas, ... accidentes de tráfico, ... Se excluyen amenazas específicas como incendio (ver [I.1]) e inundación (ver [I.2]). Se excluye al personal por cuanto se ha previsto una amenaza específica, [E.31], para cubrir la indisponibilidad involuntaria del personal sin entrar en sus causas. Origen: Entorno (accidental) Humano (accidental o deliberado) Ver: EBIOS: 04 - SINIESTRO MAYOR',null);
INSERT INTO tipo_amenaza (pk,cod,nombre,descripcion,fk_superior) VALUES (10,'I.3','Contaminación mecánica','vibraciones, polvo, suciedad, ... Origen: Entorno (accidental) Humano (accidental o deliberado) Ver: EBIOS: 03 – CONTAMINACIÓN',9);
INSERT INTO tipo_amenaza (pk,cod,nombre,descripcion,fk_superior) VALUES (11,'I.4','Contaminación electromagnética','interferencias de radio, campos magnéticos, luz ultravioleta, ... Origen: Entorno (accidental) Humano (accidental o deliberado) Ver: EBIOS: 14 - EMISIONES ELECTROMAGNÉTICAS 15- RADIACIONES TÉRMICAS 16 - IMPULSOS ELECTROMAGNÉTICOS',9);
INSERT INTO tipo_amenaza (pk,cod,nombre,descripcion,fk_superior) VALUES (12,'I.5','Avería de origen físico o lógico','fallos en los equipos y/o fallos en los programas. Puede ser debida a un defecto de origen o sobrevenida durante el funcionamiento del sistema. En sistemas de propósito específico, a veces es difícil saber si el origen del fallo es físico o lógico; pero para las consecuencias que se derivan, esta distinción no suele ser relevante. Origen: Entorno (accidental) Humano (accidental o deliberado) Ver: EBIOS: 28 - AVERÍA DEL HARDWARE 29 - FALLA DE FUNCIONAMIENTO DEL HARDWARE',9);
INSERT INTO tipo_amenaza (pk,cod,nombre,descripcion,fk_superior) VALUES (13,'I.6','Corte del suministro eléctrico','cese de la alimentación de potencia Origen: Entorno (accidental) Humano (accidental o deliberado) Ver: EBIOS: 12 - PÉRDIDA DE SUMINISTRO DE ENERGÍA',9);
INSERT INTO tipo_amenaza (pk,cod,nombre,descripcion,fk_superior) VALUES (14,'I.7','Condiciones inadecuadas de temperatura y/o humedad','deficiencias en la aclimatación de los locales, excediendo los márgenes de trabajo de los equipos: excesivo calor, excesivo frío, exceso de humedad, ... Origen: Entorno (accidental) Humano (accidental o deliberado) Ver: EBIOS: 11- FALLAS EN LA CLIMATIZACIÓN',9);
INSERT INTO tipo_amenaza (pk,cod,nombre,descripcion,fk_superior) VALUES (15,'I.8','Fallo de servicios de comunicaciones','cese de la capacidad de transmitir datos de un sitio a otro. Típicamente se debe a la destrucción física de los medios físicos de transporte o a la detención de los centros de conmutación, sea por destrucción, detención o simple incapacidad para atender al tráfico presente. Origen: Entorno (accidental) Humano (accidental o deliberado) Ver: EBIOS: 13 - PÉRDIDA DE LOS MEDIOS DE TELECOMUNICACIÓN',9);
INSERT INTO tipo_amenaza (pk,cod,nombre,descripcion,fk_superior) VALUES (16,'I.9','Interrupción de otros servicios y suministros esenciales','otros servicios o recursos de los que depende la operación de los equipos; por ejemplo, papel para las impresoras, toner, refrigerante, ... Origen: Entorno (accidental) Humano (accidental o deliberado) Ver: EBIOS: no disponible',9);
INSERT INTO tipo_amenaza (pk,cod,nombre,descripcion,fk_superior) VALUES (17,'I.10','Degradación de los soportes de almacenamiento de la información','como consecuencia del paso del tiempo Origen: Entorno (accidental) Humano (accidental o deliberado) Ver: EBIOS: 28 - AVERÍA DEL HARDWARE 29 - FALLA DE FUNCIONAMIENTO DEL HARDWARE',9);
INSERT INTO tipo_amenaza (pk,cod,nombre,descripcion,fk_superior) VALUES (18,'I.11','Emanaciones electromagnéticas','hecho de poner vía radio datos internos a disposición de terceros. Es una amenaza donde el emisor es víctima pasiva del ataque. Prácticamente todos los dispositivos electrónicos emiten radiaciones al exterior que pudieran ser interceptadas por otros equipos (receptores de radio) derivándose una fuga de información. Esta amenaza se denomina, incorrecta pero frecuentemente, ataque TEMPEST (del inglés “Transient Electromagnetic Pulse Standard”). Abusando del significado primigenio, es frecuente oír hablar de que un equipo disfruta de "TEMPEST protection", queriendo decir que se ha diseñado para que no emita, electromagnéticamente, nada de interés por si alguien lo captara. No se contempla en esta amenaza la emisión por necesidades del medio de comunicación: redes inalámbricas, enlaces de microondas, etc. que estarán amenazadas de interceptación. Origen: Entorno (accidental) Humano (accidental o deliberado) Ver: EBIOS: 17 - INTERCEPTACIÓN DE SEÑALES PARÁSITAS COMPROMETEDORAS',9);
INSERT INTO tipo_amenaza (pk,cod,nombre,descripcion,fk_superior) VALUES (19,'E','Errores y fallos no intencionados','Fallos no intencionales causados por las personas. La numeración no es consecutiva, sino que está alineada con los ataques deliberados, muchas veces de naturaleza similar a los errores no intencionados, difiriendo únicamente en el propósito del sujeto. Origen: Humano (accidental) Ver correlación de errores y amenazas. 5.3.1. [E.1] Errores de los usuarios',null);
INSERT INTO tipo_amenaza (pk,cod,nombre,descripcion,fk_superior) VALUES (20,'E.1','Errores de los usuarios','equivocaciones de las personas cuando usan los servicios, datos, etc. Ver: EBIOS: 38 - ERROR DE USO',19);
INSERT INTO tipo_amenaza (pk,cod,nombre,descripcion,fk_superior) VALUES (21,'E.2','Errores del administrador','equivocaciones de personas con responsabilidades de instalación y operación Ver: EBIOS: 38 - ERROR DE USO',19);
INSERT INTO tipo_amenaza (pk,cod,nombre,descripcion,fk_superior) VALUES (22,'E.3','Errores de monitorización (log)','inadecuado registro de actividades: falta de registros, registros incompletos, registros incorrectamente fechados, registros incorrectamente atribuidos, ... Ver: EBIOS: no disponible',19);
INSERT INTO tipo_amenaza (pk,cod,nombre,descripcion,fk_superior) VALUES (23,'E.4','Errores de configuración','introducción de datos de configuración erróneos. Prácticamente todos los activos dependen de su configuración y ésta de la diligencia del administrador: privilegios de acceso, flujos de actividades, registro de actividad, encaminamiento, etc. Ver: EBIOS: no disponible',19);
INSERT INTO tipo_amenaza (pk,cod,nombre,descripcion,fk_superior) VALUES (24,'E.7','Deficiencias en la organización','cuando no está claro quién tiene que hacer exactamente qué y cuándo, incluyendo tomar medidas sobre los activos o informar a la jerarquía de gestión. Acciones descoordinadas, errores por omisión, etc. Ver: EBIOS: no disponible',19);
INSERT INTO tipo_amenaza (pk,cod,nombre,descripcion,fk_superior) VALUES (25,'E.8','Difusión de software dañino','propagación inocente de virus, espías (spyware), gusanos, troyanos, bombas lógicas, etc. Ver: EBIOS: no disponible',19);
INSERT INTO tipo_amenaza (pk,cod,nombre,descripcion,fk_superior) VALUES (26,'E.9','Errores de [re-]encaminamiento','envío de información a través de un sistema o una red usando, accidentalmente, una ruta incorrecta que lleve la información a donde o por donde no es debido; puede tratarse de mensajes entre personas, entre procesos o entre unos y otros. Es particularmente destacable el caso de que el error de encaminamiento suponga un error de entrega, acabando la información en manos de quien no se espera. Ver: EBIOS: no disponible',19);
INSERT INTO tipo_amenaza (pk,cod,nombre,descripcion,fk_superior) VALUES (27,'E.10','Errores de secuencia','alteración accidental del orden de los mensajes transmitidos. Ver: EBIOS: no disponible',19);
INSERT INTO tipo_amenaza (pk,cod,nombre,descripcion,fk_superior) VALUES (28,'E.14','Escapes de información','la información llega accidentalmente al conocimiento de personas que no deberían tener conocimiento de ella, sin que la información en sí misma se vea alterada.',19);
INSERT INTO tipo_amenaza (pk,cod,nombre,descripcion,fk_superior) VALUES (29,'E.15','Alteración accidental de la información','alteración accidental de la información. Esta amenaza sólo se identifica sobre datos en general, pues cuando la información está en algún soporte informático, hay amenazas específicas. Ver: EBIOS: no disponible',19);
INSERT INTO tipo_amenaza (pk,cod,nombre,descripcion,fk_superior) VALUES (30,'E.18','Destrucción de información','pérdida accidental de información. Esta amenaza sólo se identifica sobre datos en general, pues cuando la información está en algún soporte informático, hay amenazas específicas. Ver: EBIOS: no disponible',19);
INSERT INTO tipo_amenaza (pk,cod,nombre,descripcion,fk_superior) VALUES (31,'E.19','Fugas de información','revelación por indiscreción. Incontinencia verbal, medios electrónicos, soporte papel, etc. Ver: EBIOS: no disponible',19);
INSERT INTO tipo_amenaza (pk,cod,nombre,descripcion,fk_superior) VALUES (32,'E.20','Vulnerabilidades de los programas (software)','defectos en el código que dan pie a una operación defectuosa sin intención por parte del usuario pero con consecuencias sobre la integridad de los datos o la capacidad misma de operar. Ver: EBIOS: no disponible',19);
INSERT INTO tipo_amenaza (pk,cod,nombre,descripcion,fk_superior) VALUES (33,'E.21','Errores de mantenimiento / actualización de programas (software)','defectos en los procedimientos o controles de actualización del código que permiten que sigan utilizándose programas con defectos conocidos y reparados por el fabricante. Ver: EBIOS: 31 - FALLA DE FUNCIONAMIENTO DEL SOFTWARE 32 - PERJUICIO A LA MANTENIBILIDAD DEL SISTEMA DE INFORMACIÓN',19);
INSERT INTO tipo_amenaza (pk,cod,nombre,descripcion,fk_superior) VALUES (34,'E.23','Errores de mantenimiento / actualización de equipos (hardware)','defectos en los procedimientos o controles de actualización de los equipos que permiten que sigan utilizándose más allá del tiempo nominal de uso. Ver: EBIOS: 32 - PERJUICIO A LA MANTENIBILIDAD DEL SISTEMA DE INFORMACIÓN',19);
INSERT INTO tipo_amenaza (pk,cod,nombre,descripcion,fk_superior) VALUES (35,'E.24','Caída del sistema por agotamiento de recursos','la carencia de recursos suficientes provoca la caída del sistema cuando la carga de trabajo es desmesurada. Ver: EBIOS: 30 - SATURACIÓN DEL SISTEMA INFORMÁTICO',19);
INSERT INTO tipo_amenaza (pk,cod,nombre,descripcion,fk_superior) VALUES (36,'E.25','Robo','la pérdida de equipos provoca directamente la carencia de un medio para prestar los servicios, es decir una indisponibilidad. Se puede perder todo tipo de equipamiento, siendo la pérdida de equipos y soportes de información los más habituales. En el caso de equipos que hospedan datos, además se puede sufrir una fuga de información. Ver: EBIOS: 22 - RECUPERACIÓN DE SOPORTES RECICLADOS O DESECHADOS',19);
INSERT INTO tipo_amenaza (pk,cod,nombre,descripcion,fk_superior) VALUES (37,'E.28','Indisponibilidad del personal','ausencia accidental del puesto de trabajo: enfermedad, alteraciones del orden público, guerra bacteriológica, ... Ver: EBIOS: 42 - DAÑO A LA DISPONIBILIDAD DEL PERSONAL',19);
INSERT INTO tipo_amenaza (pk,cod,nombre,descripcion,fk_superior) VALUES (38,'A','Ataques','Intencionados. Fallos deliberados causados por las personas.La numeración no es consecutiva para coordinarla con los errores no intencionados, muchas veces de naturaleza similar a los ataques deliberados, difiriendo únicamente en el propósito del sujeto.Origen: Humano (deliberado)',null);
INSERT INTO tipo_amenaza (pk,cod,nombre,descripcion,fk_superior) VALUES (8,'A.3','Manipulación de los registros de actividad (log)','Ver: EBIOS: no disponible',38);
INSERT INTO tipo_amenaza (pk,cod,nombre,descripcion,fk_superior) VALUES (39,'A.4','Manipulación de la configuración','prácticamente todos los activos dependen de su configuración y ésta de la diligencia del administrador: privilegios de acceso, flujos de actividades, registro de actividad, encaminamiento, etc. Ver: EBIOS: no disponible',38);
INSERT INTO tipo_amenaza (pk,cod,nombre,descripcion,fk_superior) VALUES (40,'A.5','Suplantación de la identidad del usuario','cuando un atacante consigue hacerse pasar por un usuario autorizado, disfruta de los privilegios de este para sus fines propios. Esta amenaza puede ser perpetrada por personal interno, por personas ajenas a la Organización o por personal contratado temporalmente. Ver: EBIOS: 40 - USURPACIÓN DE DERECHO',38);
INSERT INTO tipo_amenaza (pk,cod,nombre,descripcion,fk_superior) VALUES (41,'A.6','Abuso de privilegios de acceso','cada usuario disfruta de un nivel de privilegios para un determinado propósito; cuando un usuario abusa de su nivel de privilegios para realizar tareas que no son de su competencia, hay problemas. Ver: EBIOS: 39 - ABUSO DE DERECHO',38);
INSERT INTO tipo_amenaza (pk,cod,nombre,descripcion,fk_superior) VALUES (42,'A.7','Uso no previsto','utilización de los recursos del sistema para fines no previstos, típicamente de interés personal: juegos, consultas personales en Internet, bases de datos personales, programas personales, almacenamiento de datos personales, etc. Ver: EBIOS: no disponible',38);
INSERT INTO tipo_amenaza (pk,cod,nombre,descripcion,fk_superior) VALUES (43,'A.8','Difusión de software dañino','propagación intencionada de virus, espías (spyware), gusanos, troyanos, bombas lógicas, etc. Ver: EBIOS: no disponible',38);
INSERT INTO tipo_amenaza (pk,cod,nombre,descripcion,fk_superior) VALUES (44,'A.9','[Re-]encaminamiento de mensajes','envío de información a un destino incorrecto a través de un sistema o una red, que llevan la información a donde o por donde no es debido; puede tratarse de mensajes entre personas, entre procesos o entre unos y otros. Un atacante puede forzar un mensaje para circular a través de un nodo determinado de la red donde puede ser interceptado. Es particularmente destacable el caso de que el ataque de encaminamiento lleve a una entrega fraudulenta, acabando la información en manos de quien no debe. Ver: EBIOS: no disponible',38);
INSERT INTO tipo_amenaza (pk,cod,nombre,descripcion,fk_superior) VALUES (45,'A.10','Alteración de secuencia','alteración del orden de los mensajes transmitidos. Con ánimo de que el nuevo orden altere el significado del conjunto de mensajes, perjudicando a la integridad de los datos afectados. Ver: EBIOS: 36 - ALTERACIÓN DE DATOS',38);
INSERT INTO tipo_amenaza (pk,cod,nombre,descripcion,fk_superior) VALUES (46,'A.11','Acceso no autorizado','el atacante consigue acceder a los recursos del sistema sin tener autorización para ello, típicamente aprovechando un fallo del sistema de identificación y autorización. Ver: EBIOS: 33 - USO ILÍCITO DEL HARDWARE',38);
INSERT INTO tipo_amenaza (pk,cod,nombre,descripcion,fk_superior) VALUES (47,'A.12','Análisis de tráfico','el atacante, sin necesidad de entrar a analizar el contenido de las comunicaciones, es capaz de extraer conclusiones a partir del análisis del origen, destino, volumen y frecuencia de los intercambios. A veces se denomina “monitorización de tráfico”. Ver: EBIOS: no disponible',38);
INSERT INTO tipo_amenaza (pk,cod,nombre,descripcion,fk_superior) VALUES (48,'A.13','Repudio','negación a posteriori de actuaciones o compromisos adquiridos en el pasado. Repudio de origen: negación de ser el remitente u origen de un mensaje o comunicación. Repudio de recepción: negación de haber recibido un mensaje o comunicación. Repudio de entrega: negación de haber recibido un mensaje para su entrega a otro. Ver: EBIOS: 41 - NEGACIÓN DE ACCIONES',38);
INSERT INTO tipo_amenaza (pk,cod,nombre,descripcion,fk_superior) VALUES (49,'A.14','Interceptación de información (escucha)','el atacante llega a tener acceso a información que no le corresponde, sin que la información en sí misma se vea alterada. Ver: EBIOS: 19 - ESCUCHA PASIVA',38);
INSERT INTO tipo_amenaza (pk,cod,nombre,descripcion,fk_superior) VALUES (50,'A.15','Modificación deliberada de la información','alteración intencional de la información, con ánimo de obtener un beneficio o causar un perjuicio. Ver: EBIOS: no disponible',38);
INSERT INTO tipo_amenaza (pk,cod,nombre,descripcion,fk_superior) VALUES (51,'A.18','Destrucción de información','eliminación intencional de información, con ánimo de obtener un beneficio o causar un perjuicio. Ver: EBIOS: no disponible',38);
INSERT INTO tipo_amenaza (pk,cod,nombre,descripcion,fk_superior) VALUES (52,'A.19','Revelación de información','revelación de información. Ver: EBIOS: 23 – DIVULGACIÓN 27 – GEOLOCALIZACIÓN 34 - COPIA ILEGAL DE SOFTWARE',38);
INSERT INTO tipo_amenaza (pk,cod,nombre,descripcion,fk_superior) VALUES (53,'A.22','Manipulación de programas','alteración intencionada del funcionamiento de los programas, persiguiendo un beneficio indirecto cuando una persona autorizada lo utiliza. Ver: EBIOS: 26 - ALTERACIÓN DE PROGRAMAS',38);
INSERT INTO tipo_amenaza (pk,cod,nombre,descripcion,fk_superior) VALUES (54,'A.22','Manipulación de los equipos','alteración intencionada del funcionamiento de los programas, persiguiendo un beneficio indirecto cuando una persona autorizada lo utiliza. Ver: EBIOS: 25 - SABOTAJE DEL HARDWARE',38);
INSERT INTO tipo_amenaza (pk,cod,nombre,descripcion,fk_superior) VALUES (55,'A.24','Denegación de servicio','la carencia de recursos suficientes provoca la caída del sistema cuando la carga de trabajo es desmesurada. Ver: EBIOS: 30 - SATURACIÓN DEL SISTEMA INFORMÁTICO',38);
INSERT INTO tipo_amenaza (pk,cod,nombre,descripcion,fk_superior) VALUES (56,'A.25','Robo','la sustracción de equipamiento provoca directamente la carencia de un medio para prestar los servicios, es decir una indisponibilidad. El robo puede afectar a todo tipo de equipamiento, siendo el robo de equipos y el robo de soportes de información los más habituales. El robo puede realizarlo personal interno, personas ajenas a la Organización o personas contratadas de forma temporal, lo que establece diferentes grados de facilidad para acceder al objeto sustraído y diferentes consecuencias. En el caso de equipos que hospedan datos, además se puede sufrir una fuga de información. Ver: EBIOS: 20 - ROBO DE SOPORTES O DOCUMENTOS 21 - ROBO DE HARDWARE',38);
INSERT INTO tipo_amenaza (pk,cod,nombre,descripcion,fk_superior) VALUES (57,'A.26','Ataque destructivo','vandalismo, terrorismo, acción militar, ... Esta amenaza puede ser perpetrada por personal interno, por personas ajenas a la Organización o por personas contratadas de forma temporal. Ver: EBIOS: 05 - DESTRUCCIÓN DE HARDWARE O DE SOPORTES',38);
INSERT INTO tipo_amenaza (pk,cod,nombre,descripcion,fk_superior) VALUES (58,'A.27','Ocupación enemiga','cuando los locales han sido invadidos y se carece de control sobre los propios medios de trabajo. Ver: EBIOS: no disponible',38);
INSERT INTO tipo_amenaza (pk,cod,nombre,descripcion,fk_superior) VALUES (59,'A.28','Indisponibilidad del personal','ausencia deliberada del puesto de trabajo: como huelgas, absentismo laboral, bajas no justificadas, bloqueo de los accesos, ... Ver: EBIOS: 42 - DAÑO A LA DISPONIBILIDAD DEL PERSONAL',38);
INSERT INTO tipo_amenaza (pk,cod,nombre,descripcion,fk_superior) VALUES (60,'A.29','Extorsión','presión que, mediante amenazas, se ejerce sobre alguien para obligarle a obrar en determinado sentido. Ver: EBIOS: no disponible',38);
INSERT INTO tipo_amenaza (pk,cod,nombre,descripcion,fk_superior) VALUES (61,'A.30','Ingeniería social (picaresca)','abuso de la buena fe de las personas para que realicen actividades que interesan a un tercero. Ver: EBIOS: no disponible',38);
