package datos.POJOS;

public class Escala {
	
	private String tipo_escala;
	private String abreviadura;
	private String magnitud;
	
	public Escala() {
		super();
		// TODO Auto-generated constructor stub
	}
	
	public String getTipo_escala() {
		return tipo_escala;
	}
	
	public String getAbreviadura() {
		return abreviadura;
	}
	
	public String getMagnitud() {
		return magnitud;
	}
	
	public void setTipo_escala(String tipo_escala) {
		this.tipo_escala = tipo_escala;
	}
	
	public void setAbreviadura(String abreviadura) {
		this.abreviadura = abreviadura;
	}
	
	public void setMagnitud(String magnitud) {
		this.magnitud = magnitud;
	}


}
